import React from 'react';
import { motion } from 'framer-motion';

const Education = () => {
  const education = [
    {
      degree: "Master of Science in Computer Science",
      institution: "University Name",
      period: "2018 - 2020",
      description: "Specialized in Artificial Intelligence and Machine Learning. Completed thesis on Deep Learning applications in Natural Language Processing.",
      achievements: [
        "GPA: 3.8/4.0",
        "Published 2 research papers",
        "Teaching Assistant for Data Structures course"
      ]
    },
    {
      degree: "Bachelor of Technology in Computer Science",
      institution: "University Name",
      period: "2014 - 2018",
      description: "Focused on software engineering and web development. Participated in multiple hackathons and coding competitions.",
      achievements: [
        "First Class with Distinction",
        "Led the college coding club",
        "Won inter-college hackathon"
      ]
    },
    // Add more education details as needed
  ];

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
      className="min-h-screen py-20"
    >
      <div className="max-w-6xl mx-auto px-4">
        <motion.h1
          initial={{ y: -20 }}
          animate={{ y: 0 }}
          className="text-4xl font-bold text-gray-800 mb-12 text-center"
        >
          Education
        </motion.h1>

        <div className="space-y-12">
          {education.map((edu, index) => (
            <motion.div
              key={index}
              initial={{ x: index % 2 === 0 ? -50 : 50, opacity: 0 }}
              animate={{ x: 0, opacity: 1 }}
              transition={{ delay: index * 0.2 }}
              className="relative pl-8 border-l-2 border-blue-500"
            >
              <div className="absolute w-4 h-4 bg-blue-500 rounded-full -left-[9px] top-0" />
              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-xl font-bold text-gray-800 mb-1">{edu.degree}</h3>
                <p className="text-blue-500 font-medium mb-1">{edu.institution}</p>
                <p className="text-gray-500 text-sm mb-4">{edu.period}</p>
                <p className="text-gray-600 mb-4">{edu.description}</p>
                <div className="space-y-2">
                  <h4 className="font-semibold text-gray-700">Key Achievements:</h4>
                  <ul className="list-disc list-inside text-gray-600 space-y-1">
                    {edu.achievements.map((achievement, achievementIndex) => (
                      <li key={achievementIndex}>{achievement}</li>
                    ))}
                  </ul>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </motion.div>
  );
};

export default Education;
